mapper.select:
SELECT

FROM smt_order_entry a

WHERE a.id = #{id}

SELECT

FROM smt_order_entry a

left join smt_order_outgo c on a.customer_no=c.customer_no and a.order_no=c.order_no and a.product_no=c.product_no and c.del_flag = #{DEL_FLAG_NORMAL}

    a.counts - IFNULL( c.counts, 0 )!=0

        and a.product_type=#{productType}


        and a.customer_no LIKE '%${customerNo}%'


        and a.product_no LIKE '%${productNo}%'


        and a.order_no LIKE '%${orderNo}%'

    and a.del_flag = #{DEL_FLAG_NORMAL}
    GROUP BY a.customer_no,a.product_no,a.order_no



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

SELECT

FROM smt_order_entry a


    a.del_flag = #{DEL_FLAG_NORMAL}

        and a.product_type=#{productType}


        and a.customer_no LIKE '%${customerNo}%'


        and a.product_no LIKE '%${productNo}%'


        and a.order_no LIKE '%${orderNo}%'




        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

select * from (SELECT one.id AS "id", one.orderNo AS "orderNo", one.orderDate AS "orderDate", one.customerNo AS "customerNo", one.customerName AS "customerName", one.productNo AS "productNo", one.counts-if(ISNULL(sum(d.counts)),0,sum(d.counts)) as "counts", one.productType AS "productType", one.packageSize AS "packageSize", one.createDate AS "createDate", one.updateDate AS "updateDate", one.remarks AS "remarks", one.delFlag AS "delFlag" FROM (SELECT a.id AS "id", a.order_no AS "orderNo", a.order_date AS "orderDate", a.customer_no AS "customerNo", b.customer_name AS "customerName", a.product_no AS "productNo", a.product_type AS "productType", sum(a.counts) as "counts", a.package_size AS "packageSize", a.create_date AS "createDate", a.create_by AS "createBy.id", a.update_date AS "updateDate", a.update_by AS "updateBy.id", a.remarks AS "remarks", a.del_flag AS "delFlag" FROM smt_order_entry a LEFT JOIN smt_sys_customer b ON a.customer_no = b.customer_no WHERE 1 = 1
AND a.product_type =#{productType}
AND a.del_flag =#{DEL_FLAG_NORMAL} GROUP BY a.customer_no, a.product_no ) one
LEFT join smt_order_online d on one.productNo=d.product_no and one.customerNo =d.customer_no and d.del_flag = #{DEL_FLAG_NORMAL}
where 1=1
and one.customerNo LIKE '%${customerNo}%'
and one.productNo LIKE '%${productNo}%'
and one.orderNo LIKE '%${orderNo}%'
and one.id in (${id})
GROUP BY one.customerNo, one.productNo
ORDER BY ${page.orderBy}
) ff where ff.counts != 0

SELECT

FROM smt_order_entry a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

mapper.update:
UPDATE smt_order_entry SET
     update_date = #{updateDate},
	update_by = #{updateBy.id},
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
