mapper.select:
SELECT

FROM smt_order_online a

WHERE a.id = #{id}

select * from (SELECT one.id as "id", one.productType as "productType", one.orderNo AS "orderNo", one.customerNo AS "customerNo", one.customerName AS "customerName", one.productNo AS "productNo", one.counts - IF ( ISNULL( sum( pro.entry_counts ) ), 0, sum( pro.entry_counts) ) AS "counts", one.createDate AS "createDate", one.updateDate AS "updateDate", one.remarks AS "remarks", one.delFlag AS "delFlag" from (SELECT a.id AS "id", a.order_no AS "orderNo", a.customer_no AS "customerNo", b.customer_name AS "customerName", a.product_no AS "productNo", a.product_type AS "productType", sum( a.counts ) AS "counts", a.create_date AS "createDate", a.create_by AS "createBy.id", a.update_date AS "updateDate", a.update_by AS "updateBy.id", a.remarks AS "remarks", a.del_flag AS "delFlag" FROM smt_order_online a LEFT JOIN smt_sys_customer b ON a.customer_no = b.customer_no WHERE 1 = 1 AND a.del_flag = 0 GROUP BY a.customer_no, a.product_type, a.product_no ) one LEFT JOIN smt_product_entry pro ON one.productNo = pro.product_no AND one.customerNo = pro.customer_no AND pro.del_flag = 0 where 1=1
and one.productType=#{productType}
GROUP BY one.customerNo,one.productNo
ORDER BY ${page.orderBy}
) ww where ww.counts!=0
and ww.customerNo LIKE '%${customerNo}%'
and ww.productNo LIKE '%${productNo}%'
ORDER BY ww.productNo

SELECT

FROM smt_order_online a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

mapper.update:
UPDATE smt_order_online SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
